<template>
  <div>
    <h2>Registro</h2>
    <form @submit.prevent="handleSubmit">
      <div class="form-group">
        <label for="nombre">Nombre</label>
        <input
          type="text"
          v-model="user.nombre"
          v-validate="'required'"
          name="nombre"
          class="form-control"
          :class="{ 'is-invalid': submitted && errors.has('nombre') }"
        />
        <div v-if="submitted && errors.has('nombre')" class="invalid-feedback">
          {{ errors.first("nombre") }}
        </div>
      </div>
      <div class="form-group">
        <label for="correo">Correo</label>
        <input
          type="text"
          v-model="user.correo"
          v-validate="'required'"
          name="correo"
          class="form-control"
          :class="{ 'is-invalid': submitted && errors.has('correo') }"
        />
        <div v-if="submitted && errors.has('correo')" class="invalid-feedback">
          {{ errors.first("correo") }}
        </div>
      </div>
      <div class="form-group">
        <label htmlFor="password">Contrase√±a</label>
        <input
          type="password"
          v-model="user.password"
          v-validate="{ required: true, min: 6 }"
          name="password"
          class="form-control"
          :class="{ 'is-invalid': submitted && errors.has('password') }"
        />
        <div
          v-if="submitted && errors.has('password')"
          class="invalid-feedback"
        >
          {{ errors.first("password") }}
        </div>
      </div>
      <div class="form-group">
        <button class="btn btn-primary" :disabled="status.registering">
          Registro
        </button>
        <img
          v-show="status.registering"
          src="data:image/gif;base64,"
        />
        <router-link to="/login" class="btn btn-link">Inicio</router-link>
      </div>
    </form>
  </div>
</template>

<script>
import { mapState, mapActions } from "vuex";

export default {
  data() {
    return {
      user: {
        nombre: "",
        correo: "",
        password: "",
      },
      submitted: false,
    };
  },
  computed: {
    ...mapState("account", ["status"]),
  },
  methods: {
    ...mapActions("account", ["register"]),
    handleSubmit(e) {
      this.submitted = true;
      this.$validator.validate().then((valid) => {
        if (valid) {
          this.register(this.user);
        }
      });
    },
  },
};
</script>
